function solve(arr){
    function intersect(a, b) {
        return a.filter((element, index) => b[index] === element).length;
    }
    let result = 0;
    if (arr.length > 1){
        for (let i = 0; i < arr.length; i++) {
            if (i === arr.length - 1){
                result += intersect(arr[i], arr[0]);
            }else{
                result += intersect(arr[i], arr[i + 1]);
            }

        }
    }

    return result;
}


console.log(solve(
    [
    ['2', '3', '4', '7', '0'],
    ['4', '0', '5', '3', '4'],
    ['2', '3', '5', '4', '2'],
    ['9', '8', '7', '5', '4']
]));

console.log(
    solve(
        [
    ['test', 'yes', 'yo', 'ho'],
    ['well', 'done', 'yo', '6'],
    ['not', 'done', 'yet', '5']
]));