function solve(arr) {

    let toSum = Number(arr[1]);
    let len = Number(arr[0]);

    let array = [];
    for (let i = 0; i < len; i++) {
        if (i >= toSum){
            let toStart = i - toSum;
            let element = 0;
            arr.slice(toStart, i + 1).forEach(e => element += e);
            array[i] = element;
        }else{
            array[i] = 1;
        }
    }
    return array;
}

console.log(solve(6, 3));